insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'SQUAT LIBERO','piedi sotto le spalle, punte leggermente aperte, scendere con il peso sui talloni fino almeno ad avere un angolo di 90° coscia/polpaccio. spingere le ginocchia in fuori durante il movimento', 1 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'SQUAT JUMP','eseguire un salto con una distensione completa del bacino in fase di risalita, per concludere la fase di volo direttamente nello squat successivo', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'SQUAT LARGO ','rispetto alla larghezza delle spalle aprire di circa un piede ogni gamba, extra ruotare di 45° le punte dei piedi', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'FRONT SQUAT ',' eseguire lo squat con il bilanciere davanti. Bilanciere appoggiato sulle spalle contraendo il deltoide in modo che il bilanciere non appoggi sulle clavicole. Posizionare i gomiti a davanzale, se la tensione su i polsi fosse eccessiva è possibile liberare le ultime 2 dita di ciascuna mano.', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'BACK SQUAT ',' eseguire lo squat con il bilanciere dietro la testa ', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'SQUAT CON MEDBALL O KETTEBELL ',' eseguire lo squat con l''attrezzo davanti all''altezza del petto ', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'SQUAT BULGARO ',' posizionarsi con un piede su un supporto posteriore rispetto al corpo in modo che sia più in alto rispetto a quello davanti. Scendere perpendicolarmente facendo attenzione a non superare con il ginocchio anteriore la punta del piede ', 1);
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'SQUAT BULGARO CON PESI ',' eseguire una squat bulgaro con un peso, per ogni mano ', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'OVERHEADSQUAT (OHS); CON BILANCIERE ',' eseguire uno squat con il bilanciere disteso sopra la testa. Le braccia devono rimanere distese, l''impugnatura è abbastanza larga in modo da riuscire a mantenere la schiena dritta durante l''esecuzione ', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'OVERHEADSQUAT CON PESO ','eseguire uno squat con il peso in alto. Le braccia devono rimanere distese durane tutta l''esecuzione', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'THRUSTER','eseguire un front squat e durante la risalita spingere il bilanciere sopra la testa', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'THRUSTER CON KETTLEBELL','portare una kettlebell sulla spalle, eseguire un thruster a braccio singolo', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'AFFONDO STATICO','posizionare i piedi paralleli alla distanza di circa un passo con il tallone del piede dietro sollevato. Scendere perpendicolarmente piegando entrambe le gambe senza superare con il ginocchio della gamba davanti la punta del piede', 1 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'AFFONDO AVANTI ALTERNATO','eseguire un affondo facendo un passo avanti ogni volta che si esegue una ripetizione', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'AFFONDO AVANTI ALTERNATO CON PESI: ','eseguire un affondo avanti con un peso n ogni mano', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'AFFONDO AVANTI ALTERNATO CON PESO IN ALTO','eseguire un affondo avanti con il peso in alto sopra la testa e le braccia distese', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'AFFONDO ALTERNATO DIETRO','eseguire un affondo facendo un passo indietro ogni volta che esegue una ripetizione', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'AFFONDO DIETRO ALTERNATO CON PESI','eseguire un affondo dietro con un peso in ogni mano', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'AFFONDO DIETRO ALTERNATO CON PESO IN ALTO','eseguire un affondo dietro con il peso in alto sopra la testa e le braccia distese', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'AFFONDO LATERALE STATICO','posizionarsi con le gambe aperte di due piedi per lato, scendere con il peso sui talloni piegando solo una gamba. Tallone appoggiato a terra schiena dritta.', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'AFFONDO LATERALE ALTERNATO CON PESO','eseguire un affondo alternato con un peso in mano all''altezza del petto', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'AFFONDO INCROCIATO STATICO','posizionati con la gamba dietro incrociata. Scendere perpendicolarmente', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'AFFONDO INCROCIATO ALTERNATO','eseguire un passo incrociato dietro ogni ripetizione', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'AFFONDO INCROCIATO ALTERNATO CON BILANCIERE DIETRO','eseguire gli affondi incrociati con il bilanciere dietro le spalle mantenendo la schiena dritta', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'AFFONDO SALTATO','posizionarsi in affondo e nello scambio delle gambe eseguire un salto atterrando in affondo con l''altra gamba avanti', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'WALL BALL','posizionarsi con la palla medica ad un braccio dal muro, tenere la palla con entrambe le mani, eseguire uno squat e nella fase di risalita lanciare la palla in alto contro il muro, ad un''altezza di almeno 2,5mt. Riprendere la palla e scendere direttamente in squat', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'RKS','posizionarsi con la kettlebell impugnata con entrambe le mani, piegare le gambe leggermente ed eseguire una spinta con il bacino verso avanti portando la kettlebell all''altezza delle spalle', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'AKS','posizionarsi con la kettlebell impugnata con entrambi le mani, piegare le gambe leggermente ed eseguire una spinta con il bacino verso avanti portando la kettlebell fino sopra la testa senza piegare le braccia', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'BURPEES','posizionarsi con i piedi sotto le spalle, appoggiare le mani al centro dei piedi, eseguire un salto verso dietro mantenendo il peso del corpo sulle braccia, come un''onda appoggiare il corpo a terra. Eseguire il movimento opposto partendo da spingere le mani contro il pavimento, staccare il petto da terra con un arco lombare, portare con un salto i piedi più larghi delle mani e riportarsi in posizione eretta terminando il movimento con un salto e batto le mani insieme dietro la testa', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'UP/DOWN','posizionarsi con i piedi sotto le spalle, appoggiare le mani al centro dei piedi, eseguire un salto verso dietro mantenendo il peso del corpo sulle braccia terminando il movimento in posizione di plank. Eseguire il movimento opposto portando i piedi più larghi delle mani e riportarsi in posizione eretta terminando il movimento con un salto e batto le mani insieme dietro la testa', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'MEZZO BURPEES','eseguire un burpees fermandosi nella posizione di squat senza riportarsi nella posizione eretta', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'BURPEES SINGOLO MANO','posizionarsi con i piedi sotto le spalle, appoggiare una sola mano al centro dei piedi, eseguire un salto verso dietro mantenendo il peso del corpo sul singolo braccio, come un''onda appoggiare il corpo a terra. Eseguire il movimento opposto partendo da spingere la mani contro il pavimento, staccare il petto da terra con un arco lombare, portare con un salto i piedi più larghi della mano e riportarsi in posizione eretta terminando il movimento con un salto e batto le mani insieme dietro la testa', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'SALTO DELLA CORDA','afferrare con entrambe le mani la corda ed eseguire dei salti singoli', 1 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'ROLL UP','posizionarsi con i piedi sotto le spalle, eseguire in accosciata, lasciarsi rotolare indietro e risalire in piedi eseguendo un salto', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'BURPEES SINGOLO GAMBA','posizionarsi con un singolo piedi, appoggiare le mani sotto le spalle, eseguire un salto verso dietro mantenendo il peso del corpo sulle braccia, come un''onda appoggiare il corpo a terra. Eseguire il movimento opposto partendo da spingere le mani contro il pavimento, staccare il petto da terra con un arco lombare, portare con un salto il piede in mezzo alle spalle e riportarsi in posizione eretta terminando il movimento con un salto e batto le mani insieme dietro la testa', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'PLANK','posizionarsi con le mani sotto le spalle e, effettuando una leggera retroversione del bacino, posizionarsi creando una panca con il corpo contraendo l''addome e i glutei', 1 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'MOUNTAIN CLIMB','posizionarsi nella posizione di plank con un piede appoggiato al pavimento avanti verso la spalla, con un salto in appoggio sulle braccia, scambiare il piede avanti', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'MOUNTAIN CLIMB DOPPIO','posizionarsi nella posizione di plank, con un salto porto entrambi i piedi verso una spalla, con un salto torno in posizione di plank, ed eseguo il movimento verso l''altra spalla', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'RUBBIT JUMP','posizionarsi in v rovesciata e, con un salto, portare i talloni ai glutei', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'CRUNCH','posizionarsi supino a terra, con le mani dietro la nuca e i gomiti aperti, i piedi appoggiati a terra, staccare testa, spalle, scapole dal pavimento', 1 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'CRUNCH ALTERNATO','posizionarsi supino a terra, con le mani dietro la nuca e i gomiti aperti, i piedi appoggiati a terra, portare la spalla verso il ginocchio alternato staccando il piede e il gomito opposto da terra', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'CRISS CROSS','posizionarsi supino a terra, portare la spalla verso il ginocchio opposto con la gamba opposta tesa, scambiare in volo', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'MANO/PIEDE','posizionarsi supino a terra  con le gambe tese avanti e le braccia distese dietro, staccando il tronco da terra e il piede opposto da terra cercare di farli toccare insieme', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'SIT UP','posizionarsi supino con le braccia distese dietro e i piedi uniti a posizione di "farfalla", da braccia distese dietro portarsi a posizione seduto toccando con le mani oltre i piedi', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'VUP FACILITATO','posizionarsi supino con le braccia distese dietro e le gambe distese avanti, sedersi e, portando simultaneamente le ginocchia verso il petto, abbracciarle senza appoggiare i piedi a terra e tornare nella posizione di partenza', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'VUP','posizionarsi supino con le braccia distese dietro e le gambe distese avanti, sedersi e, portando simultaneamente le gambe distese verso l''alto, toccare le punte dei piedi e tornare alla posizione di partenza', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'HOLLOW','posizionarsi supino con le gambe distese avanti e le braccia distese dietro stacco gambe e testa spalle e scapole cercando il più possibile di mantenere la zona lombare appoggiata a terra con una leggera retroversione in posizione di "cucchiaio", eseguire oscillazioni', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'RUSSIAN TWIST','posizionarsi seduto con le ginocchia flesse, stacco i piedi da terra e con un peso in mano lo appoggio lateralmente verso destra e verso sinistra senza muovere le gambe', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'RUSSIAN TWIST CON PALLA','eseguire russian twist con una medball come peso', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'ADDOMINALI PALLA FACILITATI','posizionarsi in plank con i piedi sulla palla, portare le ginocchia verso il petto e distenderle', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'ADDOMINALI PALLA','posizionarsi in plank con i piedi sulla palla, portare i glutei verso il soffitto cercando di posizionarsi a "V" e ritornare nella posizione di partenza', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'CRUNCH SU PALLA','sdraiarsi supini sulla palla con le scapole appoggiste sulla palla, eseguire un crunch', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'ROLL OUT','con l''ausilio di un bilanciere, posizionarsi con le mani su questo e le ginocchia a terra, con una forte retroversione del bacino partendo da un''ipotetica posizione di "gatto" far rotolare il bilanciere in avanti avvicinando il ventre verso il pavimento', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'CRUNCH INVERSO CON PESO','posizionarsi supini a terra con una kettlebell tra le mani, posizionando le gambe perpendicolarmente al pavimento e al busto portare le tibie verso la kettlebell', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'PONTE','posizionarsi supino con i piedi completamente appoggiati al pavimento, staccare i glutei fino alle scapole e tornare nella posizione di partenza', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'PONTE SINGOLO','eseguire il ponte con un solo piede a spinta e l''altra gamba distesa in alto', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'CALCI AVANTI','eseguire dei calci avanti il più alti possibile alternati', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'DEADLIFT','posizionarsi con i piedi alla larghezza delle spalle e le punte dei piedi leggermente extraruotate, piegare le gambe cercando di non far scendere i glutei sotto la linea delle ginocchia mantenendo la schiena dritta e le scapole addotte. Afferrare il bilanciere e staccare il bilanciere da terra senza mai piegare le braccia, facendo passare il bilanciere vicino alle gambe in linea retta, riportare il bilanciere a terra avendo cura di mantenere la schiena dritta e le ginocchia leggermente piegate', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'DEADLIFT SINGOLO','posizionandosi in posizione eretta con la kettlebell già staccata da terra afferrata da una mano sola. Eseguire un deadlift a gamba singola mantenendo a terra il piede opposto alla mano con il peso, mantenere l''altra gamba distesa dietro senza mai muoverla', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'MBD','posizionarsi in posizione eretta con la palla al centro dei piedi. Eseguire un deadlift in velocità portando la palla su un spalla, lasciarla cadere a terra e ripeterlo poi portando la palla all''altra spalla', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'PUSH UP GINOCCHIA','posizionarsi in plank con le ginocchio appoggiate a terra, eseguire un piegamento sulle braccia fino ad arrivare ad appoggiare il petto a terra e risalire avendo cura di non perdere mai il controllo addominale', 1 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'PUSH UP','posizionarsi in plank, eseguire un piegamento sulle braccia fino ad arrivare ad appoggiare il petto a terra e risalire avendo cura di non perdere mai il controllo addominale', 1 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'PUSH UP SINGOLO','posizionarsi in plank con una mano al centro dei piedi, ruotare il bacino verso la mano staccata ed eseguire un push up', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'WAVE','posizionarsi in plank, alzare i glutei in posizione di V rovesciata , avvicinare gradualmente il corpo a terra partendo dal petto fino al bacino, ritornare eseguendo il movimento opposto', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'DIP','posizionarsi con le mani su un supporto posteriore rispetto al corpo e le dita girate verso il bacino. Avvicinare i glutei al pavimento piegando i gomiti. Mantenere la schiena vicina al supporto durante il movimento', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'DIP A GAMBE TESE','eseguire un dip con le gambe distese avanti senza muovere il bacino', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'DIP ALLE CORNA','eseguire un dip alle corna', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'DIP AGLI ANELLI','eseguire un dip agli anelli', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'PRESS CON BILACIERE','posizionare il bilanciere all''altezza delle spalle con i gomiti in basso e portarlo sopra la testa raddrizzando le braccia', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'PUSH PRESS','posizionarsi con il bilanciere sulle spalle con i gomiti verso il basso e le ginocchia piegate leggermente in avanti, portare il bilanciere sopra la testa con l''ausilio di un leggero salto', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'PRESS CON KETTLEBELL','eseguire un press con una mano sola portando il peso dalla spalla a sopra la testa', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'PUSH PRESS CON KETTLEBELL','eseguire un push press con una mano sola portando il peso dalla spalla a sopra la testa', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'HSPU','posizionarsi in verticale contro il muro, piegare le braccia a ed appoggiare la testa contro il pavimento e tornare nella posizione di partenza distendendo le braccia', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'PULL UP','ppendersi alla sbarra con le braccia tese, eseguire una trazione portando il petto alla sbarra e tornare alla posizione di partenza', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'SLANCI LATERALE SUL FIANCO','posizionarsi in decubito laterale ed eseguire uno slancio a gambe tese della gamba in alto', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'ABDUZIONE GAMBE IN QUADRUPEDIA','posizionarsi in quadrupedia ed eseguire uno slancio controllato della gamba laterale', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'ELASTICO INCROCIATO IN PIEDI SLANCIO LATERALE','posizionarsi con l''elastico sotto i piedi e con i piedi alla larghezza delle spalle, eseguire degli slanci laterali con gamba tesa', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'ELASTICO INCROCIATO IN PIEDI SLANCIO INDIETRO','posizionarsi con l''elastico sotto i piedi e con i piedi alla larghezza delle spalle, eseguire degli slanci verso dietro con gamba tesa', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'STEP UP','posizionarsi in piedi sul cubo scendere con una gamba dal cubo controllando il movimento e facendo in modo che il piedi non tocchi terra. Scendere il più possibile', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'STEP UP CON PESI','eseguire uno step up con una kettlebell per mano', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'STEP UP DA TERRA','posizionarsi davanti ad un cubo, mettere un piede sul cubo e salire e scendere senza mai appoggiare l''altro piede sul cubo e sequenzialmente senza mai staccare il piede dal cubo', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'STEP UP DA TERRA CON PESI','eseguire degli step up con un peso per mano', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'AFFONDI LATERALI SUL CUBO','appoggiare un piede extraruotato sul cubo ed eseguire un affondo laterale', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'SALI SCENDI','posizionarsi davanti un cubo e salire e scendere', 0 );
insert into  Esercizi (NomeEsercizio,Descrizione,IsRiscaldamento)values( 'BOX JUMP','posizionarsi davanti il cubo e saltarci sopra a piedi pari avendo cura di appoggiare tutto il piede sul cubo alla larghezza delle spalle, distendere le gambe e scendere saltando', 0 );
